import './cadastro.css';
import { useState } from 'react';
import { useReducer } from 'react';

const emailReducer =(state,action)=>{
    if(action.type ==='USER_INPUT'){
      return{valor:action.value,eValido:action.value.includes('@')}
    }
    if(action.type==='INPUT_BLUR'){
      return{valor:state.valor,eValido:state.valor.includes('@')}
    }
    return{valor:'',evalido:false}
  }

function Cadastro(){
    const[email,setEmail]=useState("")
    const[termos,setEtermos]=useState(false)
    const[nomeUtilizador,setNomeUtilizador]=useState("")
    const[password,setPassword]=useState("")

    const [campomail,dispatchEmail]= useReducer(emailReducer,{
        valor:'',
        eValido:false,
      });
    const emailChangeHandler = (event) => {
        dispatchEmail({type:'USER_INPUT',val:event.target.value})
      };

    const validateEmailHandler = () => {
          dispatchEmail({type:'INPUT_BLUR'})
        };

    const submithandler=()=>{
        console.log(email)
    }
    return(
        <form className="cadastrar-card" onSubmit={submithandler}>
            <div className="content-controler">
                <label className="content-description"><p>Nome Utilizador</p></label>
                <input type="text" classname="input-controler" value={campomail.valor}
                    onChange={emailChangeHandler}
                    onBlur={validateEmailHandler}
            ></input>
            </div>
            <div className="content-controler">
                <label className="content-description"><p>Email</p></label>
                <input type="mail" classname="input-controler"></input>
            </div>
            <div className="content-controler">
                <label className="content-description"><p>Password</p></label>
                <input type="password" classname="input-controler"></input>
            </div>
            <div className="content-controler">
                <label className="content-description"><p>Confirmar password</p></label>
                <input type="password" className="input-controler"></input>
            </div>
            <div className="content-controler">
                <button type="submit" onclick={submithandler} > cadastrar</button>
            </div>
            <div className="content-controler-termos">
                <div  className="termos-utilizacao">
                    <input type="checkbox"></input>
                    <p className="termos">Li e aceito os</p>
                    <p className="termos">Termos de Utilizacao</p>
                </div>

            </div>



        </form>
    )
}
export default Cadastro
