import './cadastro.css';
import { useState } from 'react';
import { useReducer } from 'react';
import axios from 'axios';
import { Navigate } from 'react-router-dom';


function Cadastro(){
    const [usersdata,setusersData]=useState()
    const submithandler=async event=>{
        event.preventDefault()
        //falta verificacoes
      try{

        if(cadastroVerifier){await axios.post("http://localhost:8800/cadastro",userdata)}

      }catch(erro){console.log(erro)}

    }

    const cadastroVerifier =async event=>{
        try{
            setusersData(await (await axios.get("http://localhost:8800/cadastro_verificicar")).data)
            let username=usersdata.some(usersdata => usersdata.NOME == userdata.nome)
            let useremail=usersdata.some(usersdata => usersdata.EMAIL == userdata.email)
            let passcodeConf=userdata.password == passwordconf
            console.log(usersdata)
            console.log( usersdata.some(usersdata => usersdata.EMAIL == userdata.email))
            console.log( usersdata.some(usersdata => usersdata.NOME == userdata.nome))
            console.log(userdata.password == passwordconf)
            console.log(passwordconf,typeof(passwordconf))
            console.log(userdata.password,typeof (userdata.password))
            console.log(userdata)
            if(!username && !useremail && passcodeConf){return true}
            //if(usersdata.includes(userdata.email)){console.log("usuario existe")}else{console.log("usuario nao existe")}

        }catch(erro){
            console.log(erro)}
    }


    const[userdata,setuserData]=useState({
        nome: "",
        email: "",
        password:"",
    })

    const handlechanger=(e)=>{
        setuserData((prev)=>({...prev,[e.target.name]:e.target.value}))
    }

    const [passwordconf,controlPasswordconf]=useState("")
    const passwordconfHandler=(event)=>{
        controlPasswordconf(event.target.value)

    }



    return(
        <form className="cadastrar-card" onSubmit={submithandler}>
            <div className="content-controler">
                <label className="content-description"><p>Nome Utilizador</p></label>
                <input type="text" className="input-controler" name="nome" onChange={handlechanger}
            ></input>
            </div>
            <div className="content-controler">
                <label className="content-description"><p>Email</p></label>
                <input type="mail" className="input-controler" name="email" onChange={handlechanger}></input>
            </div>
            <div className="content-controler">
                <label className="content-description"><p>Password</p></label>
                <input type="password" className="input-controler" name="password" onChange={handlechanger}></input>
            </div>
            <div className="content-controler">
                <label className="content-description"><p>Confirmar password</p></label>
                <input type="password" className="input-controler" onChange={passwordconfHandler}></input>
            </div>
            <div className="content-controler">
                <button type="submit"> cadastrar</button>
            </div>
            <div className="content-controler-termos">
                <div  className="termos-utilizacao">
                    <input type="checkbox"></input>
                    <p className="termos">Li e aceito os</p>
                    <p className="termos">Termos de Utilizacao</p>
                </div>

            </div>



        </form>
    )
}
export default Cadastro
