import "./cadastro.css"
import { useState } from "react"
import axios from "axios"

function Login(){

    const[userdata,setuserData]=useState({
        nome: "",
        password:"",
    })
    const handlechanger=(e)=>{
        setuserData((prev)=>({...prev,[e.target.name]:e.target.value}))
    }

    const submithandler=async event=>{
        event.preventDefault()
        let verefied_email
        let verified_name
        //falta verificacoes
        try{
            console.log((await axios.post("http://localhost:8800/login_verefier",userdata)).data)
        }catch(erro){
            console.log(erro)
        }
        }


    return(
        <div className='cadastrar-div'>
            <form className="cadastrar-card" onSubmit={submithandler}>
                <div className='titulo'><h2>Login</h2></div>
                <div className="content-controler">
                    <label className="content-description">Nome de Utilizador</label>
                    <input type="text" className="input-controler " onChange={handlechanger}></input>
                </div>
                <div className="content-controler">
                    <label className="content-description">Password</label>
                    <input type="password" className="input-controler " onChange={handlechanger}></input>
                </div>
                <div className="content-controler">
                <button type="submit" className='cadastrar-button'>Login</button>
            </div>
            </form>
        </div>
    )
}
export default Login
