      cover:'/Users/diegosousa/Documents/GitHub/MindeloCine/mindelocine-server/pictures/infiltrado.jpeg',
      preco:'250$00',
      disponiveis:'40'
    },
    {
      id: 3,
      title:  'Um filme qualquer I',
      openingText: 'Este é o segundo texto de abertura do filme',
